import React, { useEffect, useState } from "react";

export default function HaveTodo({ newTodo }) {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    const newT = newTodo;
    setTodos([...todos, newT]);
  }, [newTodo]);
  return (
    <div className="section">
      <div className="todoTitle">해야 할 일</div>
      <div>
        {todos !== null
          ? todos.map((todo) => {
              return (
                <>
                  <div key={Math.random()}>{todo}</div>;
                </>
              );
            })
          : null}
      </div>
    </div>
  );
}
