import React, { useEffect, useState } from "react";

export default function HaveTodo({ newTodo }) {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    const newT = newTodo;
    setTodos([...todos, newT]);
  }, []);
  return (
    <>
      <div className="todoTitle">해야 할 일</div>
      <div>
        {todos !== null
          ? todos.map((todo) => {
              return <div>{todo}</div>;
            })
          : null}
      </div>
    </>
  );
}
