import React, { useEffect, useState } from "react";

export default function HaveTodo({ newTodo = "null" }) {
  const [todos, setTodos] = useState(null);

  useEffect(() => {
    const newT = newTodo;
    setTodos([...todos, newT]);
  }, [newTodo]);
  return (
    <div className="section">
      <div className="todoTitle">해야 할 일</div>
      <div>
        {todos !== null
          ? todos.map((todo) => {
              return (
                <div class="todo">
                  <div key={Math.random()}>{todo}</div>
                  <button>완료</button>
                </div>
              );
            })
          : null}
      </div>
    </div>
  );
}
