import React, { useEffect, useState } from "react";

export default function Input({ createTodo }) {
  const handleTodo = (e) => {
    setUserInput(e.target.value);
    if (e.keycode === 13) {
      createTodo(userInput);
    }
  };
  return (
    <input
      placeholder="UMC 스터디 계획을 작성해보세요!"
      onKeyDown={handleTodo}
    ></input>
  );
}
