import React, { useEffect, useState } from "react";

export default function HaveTodo({ todo, handleTodo, handleDone }) {
  const todos = todo;

  //   const removeTodo = (e) => {
  //     handleDone(todos[parseInt(e.target.parentElement.id)].content);
  //     setTodos((todos) =>
  //       todos.filter((todo) => todo.id !== parseInt(e.target.parentElement.id))
  //     );
  //   };

  const removeTodo = (e) => {
    const idToRemove = parseInt(e.target.parentElement.id);
    const todoToRemove = todos.find((todo) => todo.id === idToRemove);
    handleTodo((prevTodos) =>
      prevTodos.filter((todo) => todo.id !== idToRemove)
    );
    handleDone(todoToRemove.content);
  };
  return (
    <div className="section">
      <div className="todoTitle">해야 할 일</div>
      <div className="lists">
        {todos.map((todo, index) => {
          return (
            <div className="todo" key={index} id={todo.id}>
              <div>{todo.content}</div>
              <button onClick={removeTodo}>완료</button>
            </div>
          );
        })}
      </div>
    </div>
  );
}
