import React, { useEffect, useState } from "react";

export default function HaveTodo({ todo, handleDone }) {
  const [todos, setTodos] = useState([]);
  useEffect(() => {
    console.log("todos: ", todos);
  }, [todos]);
  useEffect(() => {
    if (todo) {
      setTodos([...todos, { content: todo, id: todos.length }]);
    }
  }, [todo]);

  const removeTodo = (e, index) => {
    setTodos(todos.filter((todo) => todo.id !== index));
  };
  return (
    <div className="section">
      <div className="todoTitle">해야 할 일</div>
      <div>
        {todos.map((todo, index) => {
          return (
            <div className="todo" key={index} id={index}>
              <div>{todo.content}</div>
              <button onClick={() => removeTodo(index)}>완료</button>
            </div>
          );
        })}
      </div>
    </div>
  );
}
