import React, { useEffect, useState } from "react";

export default function HaveTodo({ newTodo }) {
  const [todos, setTodos] = useState([]);
  useEffect(() => {
    console.log("todos: ", todos);
  }, [todos]);
  useEffect(() => {
    if (newTodo) {
      const newT = newTodo;
      setTodos([...todos, { content: newT, id: todos.length + 1 }]);
    }
  }, [newTodo]);

  const removeTodo = (e) => {
    console.log(e.target.parentElement);
    setTodos(todos.filter((id) => e.target.parentElement.id !== id.id));
  };
  return (
    <div className="section">
      <div className="todoTitle">해야 할 일</div>
      <div>
        {todos.length > 0 &&
          todos.map((todo, index) => {
            return (
              <div class="todo" key={index} id={index}>
                <div>{todo.content}</div>
                <button onClick={removeTodo} key={index}>
                  완료
                </button>
              </div>
            );
          })}
      </div>
    </div>
  );
}
